
public class Predator {
	int health = 0;
	int attack = 0;
	int speed = 0;
	
	public Predator() {
		// TODO Auto-generated constructor stub
	}

	public int getHealth() {
		return health;
	}

	public void setHealth(int health) {
		this.health = health;
	}

	public int getAttack() {
		return attack;
	}

	public void setAttack(int attack) {
		this.attack = attack;
	}

	public int getSpeed() {
		return speed;
	}

	public void setSpeed(int speed) {
		this.speed = speed;
	}

}
